INSERT INTO `oc_categories` VALUES(32, 14, '1', '1', 2, 'A Level Philosophy Courses', '', 'a-level-philosophy-courses', 87, '<p><font size="2" face="tahoma,arial,helvetica,sans-serif">This A Level Philosophy course has been designed to enable students to gain a thorough grounding in key philosophical concepts, themes, texts and techniques. Students will develop a range of transferable skills which can be applied far beyond the study of Philosophy. At AS level, the course concentrates on a number of key philosophical themes, intended to provide students with a broad introduction to Philosophy. At A2, students will specialise further, selecting two themes to study in depth and focusing on philosophical problems through the study of a key text.</font></p>\n<p><font size="2" face="tahoma,arial,helvetica,sans-serif">This course allows you to study at your own pace, and is suitable to be studied by all students irrespective of age, creed, religion or gender.</font></p>\n<p><font size="2" face="Tahoma"><strong>To enrol call us on 0121 2880181.</strong></font></p>', '', 0, NULL, '', '', '', '2013-06-26 22:10:42', '0');
