INSERT INTO `oc_categories` VALUES(44, 14, '1', '1', 9, 'Health and Safety Courses', '', 'health-and-safety-courses1', 112, '<p align="justify"><font color="#666666"><font size="2" face="tahoma,arial,helvetica,sans-serif"><strong><font color="#ff0000">UK Open College delivers Health and Safety courses</font></strong> which are simple and easy to use, making training quick and effective. We provide online training courses for </font><font size="2" face="tahoma,arial,helvetica,sans-serif">food safety</font><font size="2" face="tahoma,arial,helvetica,sans-serif">, </font><font size="2" face="tahoma,arial,helvetica,sans-serif">fire safety</font><font size="2" face="tahoma,arial,helvetica,sans-serif">, </font><font size="2" face="tahoma,arial,helvetica,sans-serif">health &amp; safety</font><font size="2" face="tahoma,arial,helvetica,sans-serif">, </font><font size="2" face="tahoma,arial,helvetica,sans-serif">VDU (Visual Display Unit) awareness</font><font size="2" face="tahoma,arial,helvetica,sans-serif">, </font><font size="2" face="tahoma,arial,helvetica,sans-serif">manual handling</font><font size="2" face="tahoma,arial,helvetica,sans-serif"> and </font><font size="2" face="tahoma,arial,helvetica,sans-serif">hazard awareness</font></font><font size="2" face="tahoma,arial,helvetica,sans-serif" color="#666666">.<br /><br /></font><font size="2" face="tahoma,arial,helvetica,sans-serif" color="#666666">We use audio visual, interactive animation to deliver our e-learning courses in an enjoyable and refreshing format. The subscription process is simple - be training within a few hours and printing your certificate in a matter of hours. All courses are independently audited by Environmental Health Officers.<br /><br />Our e-learning courses are innovative and user-friendly training products. Our Food Safety, Fire Safety, Workstations and VDUs, Manual Handling and Health &amp; Safety courses are available through this website.</font></p>', '', 0, NULL, '', '', '', '2013-06-26 23:11:49', '0');
